import React, { useEffect, useState } from 'react';
import { loadSentencesFromCSV, Sentence } from '../utils/parseCSV';
import { motion, AnimatePresence } from 'framer-motion';

const SyllableStepper: React.FC = () => {
  const [sentences, setSentences] = useState<Sentence[]>([]);
  const [currentSentenceIndex, setCurrentSentenceIndex] = useState(0);
  const [currentSyllableIndex, setCurrentSyllableIndex] = useState(0);

  // Load CSV once
  useEffect(() => {
    fetch('/STS_practice_sentences.csv')
      .then(res => res.text())
      .then(text => {
        const result = loadSentencesFromCSV(text);
        setSentences(result);
      });
  }, []);

  const nextSyllable = () => {
    const sentence = sentences[currentSentenceIndex];
    if (!sentence) return;

    // If not at end of current sentence, move to next syllable
    if (currentSyllableIndex < sentence.flattenedSyllables.length - 1) {
      setCurrentSyllableIndex(prev => prev + 1);
    } 
    // Else go to next sentence if any
    else if (currentSentenceIndex < sentences.length - 1) {
      setCurrentSentenceIndex(prev => prev + 1);
      setCurrentSyllableIndex(0);
    }
  };

  // Spacebar listener
  useEffect(() => {
    const handleKey = (e: KeyboardEvent) => {
      if (e.code === 'Space') {
        e.preventDefault();
        nextSyllable();
      }
    };
    window.addEventListener('keydown', handleKey);
    return () => window.removeEventListener('keydown', handleKey);
  }, [sentences, currentSentenceIndex, currentSyllableIndex]);

  if (sentences.length === 0) {
    return <p>Loading sentences...</p>;
  }

  const currentSentence = sentences[currentSentenceIndex];
  const totalSyllables = currentSentence.flattenedSyllables.length;
  const currentSyllable = currentSentence.flattenedSyllables[currentSyllableIndex];

  // Which word are we on?
  const { wordIndex: currentWordIndex } = currentSentence.mapping[currentSyllableIndex];

  // Build the entire sentence from the original words
  // We'll color each word based on whether it's completed, current, or future
  const sentenceDisplay = currentSentence.words.map((w, idx) => {
    // Count how many syllables are in this word
    const wordSyllCount = w.syllables.length;
    // Count how many total syllables have passed before this word
    const syllablesBeforeThisWord = currentSentence.words
      .slice(0, idx)
      .reduce((acc, word) => acc + word.syllables.length, 0);

    // If the user has advanced beyond all syllables in this word, it's completed
    const wordLastSyllableIndex = syllablesBeforeThisWord + wordSyllCount - 1;

    // If we've advanced beyond that last syllable, the entire word is "completed"
    if (currentSyllableIndex > wordLastSyllableIndex) {
      return (
        <span key={idx} style={{ color: 'green', marginRight: '0.5rem' }}>
          {w.word}
        </span>
      );
    }

    // If we are currently in this word
    if (idx === currentWordIndex) {
      return (
        <span key={idx} style={{ color: '#000', fontWeight: 'bold', marginRight: '0.5rem' }}>
          {w.word}
        </span>
      );
    }

    // Otherwise, it's a future word
    return (
      <span key={idx} style={{ color: 'rgba(0,0,0,0.5)', marginRight: '0.5rem' }}>
        {w.word}
      </span>
    );
  });

  // Progress bar ratio
  const progressRatio = (currentSyllableIndex + 1) / totalSyllables;

  return (
    <div style={{ textAlign: 'center' }}>
      <p className="syllable-count" style={{ marginBottom: '0.5rem' }}>
        Syllable {currentSyllableIndex + 1} of {totalSyllables}
      </p>

      {/* Progress Bar */}
      <div
        style={{
          width: '80%',
          height: '10px',
          background: '#ddd',
          borderRadius: '5px',
          margin: '1rem auto',
          overflow: 'hidden'
        }}
      >
        <div
          style={{
            width: `${progressRatio * 100}%`,
            height: '100%',
            background: '#4caf50',
            transition: 'width 0.3s ease'
          }}
        ></div>
      </div>

      {/* Display the entire sentence as words */}
      <div style={{ marginBottom: '2rem', fontSize: '1.5rem' }}>
        {sentenceDisplay}
      </div>

      {/* Animated Syllable Box */}
      <div style={{ display: 'flex', flexDirection: 'column', alignItems: 'center' }}>
        <AnimatePresence mode="wait">
          <motion.div
            key={currentSyllable}
            className="syllable-box"
            initial={{ opacity: 0, scale: 0.8 }}
            animate={{ opacity: 1, scale: 1 }}
            exit={{ opacity: 0, scale: 0.8 }}
            transition={{ duration: 0.3 }}
            style={{
              display: 'flex',
              alignItems: 'center',
              justifyContent: 'center',
              minHeight: '120px',
              minWidth: '250px',
              textAlign: 'center'
            }}
          >
            {currentSyllable}
          </motion.div>
        </AnimatePresence>

        {/* Restart Button */}
        <button
          style={{
            marginTop: '2rem',
            padding: '1rem 2rem',
            fontSize: '1rem',
            background: '#1c3faa',
            color: 'white',
            border: 'none',
            borderRadius: '1rem',
            cursor: 'pointer',
            boxShadow: '0 4px 10px rgba(0, 0, 0, 0.1)'
          }}
          onClick={() => setCurrentSyllableIndex(0)}
        >
          üîÅ Restart Sentence
        </button>
      </div>
    </div>
  );
};

export default SyllableStepper;
