import React, { useEffect, useState } from 'react';
import { loadSentencesFromCSV, Sentence } from '../utils/parseCSV';
import { motion, AnimatePresence } from 'framer-motion';

const SyllableStepper: React.FC = () => {
  const [sentences, setSentences] = useState<Sentence[]>([]);
  const [currentSentenceIndex, setCurrentSentenceIndex] = useState(0);
  const [currentSyllableIndex, setCurrentSyllableIndex] = useState(0);

  // Load CSV once on component mount
  useEffect(() => {
    fetch('/STS_practice_sentences.csv')
      .then(res => res.text())
      .then(text => {
        const result = loadSentencesFromCSV(text);
        setSentences(result);
      });
  }, []);

  // Function to move to the next syllable
  const nextSyllable = () => {
    const current = sentences[currentSentenceIndex];
    if (!current) return;

    // If not at the end of the sentence, go to next syllable
    if (currentSyllableIndex < current.syllables.length - 1) {
      setCurrentSyllableIndex(prev => prev + 1);
    } 
    // If at the end of the sentence, move to next sentence
    else if (currentSentenceIndex < sentences.length - 1) {
      setCurrentSentenceIndex(prev => prev + 1);
      setCurrentSyllableIndex(0);
    }
  };

  // Listen for spacebar presses
  useEffect(() => {
    const handleKey = (e: KeyboardEvent) => {
      if (e.code === 'Space') {
        e.preventDefault();
        nextSyllable();
      }
    };
    window.addEventListener('keydown', handleKey);
    return () => window.removeEventListener('keydown', handleKey);
  }, [sentences, currentSentenceIndex, currentSyllableIndex]);

  if (sentences.length === 0) {
    return <p>Loading sentences...</p>;
  }

  const currentSentence = sentences[currentSentenceIndex];
  const currentSyllable = currentSentence.syllables[currentSyllableIndex];

  // Helper to style each syllable based on progress
  const getSyllableStyle = (idx: number) => {
    if (idx < currentSyllableIndex) {
      // Already spoken
      return { color: 'green', opacity: 1 };
    } else if (idx === currentSyllableIndex) {
      // Current syllable
      return { color: '#000', fontWeight: 'bold', opacity: 1 };
    } else {
      // Future syllable
      return { color: 'rgba(0,0,0,0.5)', opacity: 0.8 };
    }
  };

  return (
    <div style={{ textAlign: 'center' }}>
      {/* Syllable Count */}
      <p className="syllable-count" style={{ marginBottom: '0.5rem' }}>
        Syllable {currentSyllableIndex + 1} of {currentSentence.syllables.length}
      </p>

      {/* Progress Bar */}
      <div
        style={{
          width: '80%',
          height: '10px',
          background: '#ddd',
          borderRadius: '5px',
          margin: '1rem auto',
          overflow: 'hidden'
        }}
      >
        <div
          style={{
            width: `${((currentSyllableIndex + 1) / currentSentence.syllables.length) * 100}%`,
            height: '100%',
            background: '#4caf50',
            transition: 'width 0.3s ease'
          }}
        ></div>
      </div>

      {/* Sentence Display (All Syllables) */}
      <div style={{ marginBottom: '2rem', fontSize: '1.5rem' }}>
        {currentSentence.syllables.map((syll, idx) => {
          const style = getSyllableStyle(idx);
          return (
            <span key={idx} style={{ ...style, marginRight: '0.3rem' }}>
              {syll}
            </span>
          );
        })}
      </div>

      {/* Animated Syllable Box */}
      <div style={{ display: 'flex', flexDirection: 'column', alignItems: 'center' }}>
        <AnimatePresence mode="wait">
          <motion.div
            key={currentSyllable}
            className="syllable-box"
            initial={{ opacity: 0, scale: 0.8 }}
            animate={{ opacity: 1, scale: 1 }}
            exit={{ opacity: 0, scale: 0.8 }}
            transition={{ duration: 0.3 }}
            style={{
              display: 'flex',
              alignItems: 'center',
              justifyContent: 'center',
              minHeight: '120px',
              minWidth: '250px',
              textAlign: 'center'
            }}
          >
            {currentSyllable}
          </motion.div>
        </AnimatePresence>

        {/* Restart Button */}
        <button
          style={{
            marginTop: '2rem',
            padding: '1rem 2rem',
            fontSize: '1rem',
            background: '#1c3faa',
            color: 'white',
            border: 'none',
            borderRadius: '1rem',
            cursor: 'pointer',
            boxShadow: '0 4px 10px rgba(0, 0, 0, 0.1)'
          }}
          onClick={() => setCurrentSyllableIndex(0)}
        >
          🔁 Restart Sentence
        </button>
      </div>
    </div>
  );
};

export default SyllableStepper;
